import { __decorate } from "tslib";
import { Component, Output, EventEmitter, } from '@angular/core';
let RecommendedProductsComponent = class RecommendedProductsComponent {
    constructor(productService) {
        this.productService = productService;
        this.selectedProduct = {
            id: 0,
            name: '',
            description: '',
            defaultImage: '',
            price: 0,
            discount: 0,
        };
        this.index = 0;
        this.addProduct = new EventEmitter();
    }
    async ngOnInit() {
        this.products = await this.productService
            .getRecommendedProducts()
            .toPromise();
        this.selectedProduct = this.products[0];
        this.timer = setInterval(() => {
            this.index === this.products.length - 1 ? (this.index = 0) : this.index++;
            this.selectedProduct = this.products[this.index];
        }, 10000);
    }
    onClick() {
        this.addProduct.emit(this.selectedProduct);
    }
    ngOnDestroy() {
        clearTimeout(this.timer);
    }
};
